import "core-js/modules/es.array.push.js";
import { getIndexData, getIndexSexData, getTaskChart, getTaskIndexList } from '../../../api/api';
import * as echarts from "echarts";
import debounce from 'lodash/debounce'; // 防抖函数

export default {
  data() {
    return {
      top: {},
      sex: [],
      tasks: [],
      nums: [],
      taskList: [],
      taskCard: [],
      today: new Date(),
      myChart: "",
      pieChart: "",
      courseSelect: '',
      courses: [{
        value: '1',
        label: '计算机科学导论'
      }, {
        value: '2',
        label: '数据结构与算法'
      }, {
        value: '3',
        label: '数据库系统原理'
      }],
      progressChart: null,
      resourceChart: null
    };
  },
  methods: {
    // 新增的四个跳转方法
    toTeacherTask() {
      this.$router.push("/teacherTask");
      this.setActiveMenu("/teacherTask", "课程管理");
    },
    toTeacherChapter() {
      this.$router.push("/teacherChapter");
      this.setActiveMenu("/teacherChapter", "章节管理");
    },
    toTeacherTest() {
      this.$router.push("/teacherTest");
      this.setActiveMenu("/teacherTest", "测试管理");
    },
    toQuestion() {
      this.$router.push("/question");
      this.setActiveMenu("/question", "题库管理");
    },
    // 公共方法设置激活菜单
    setActiveMenu(path, name) {
      const param = {
        "name": name,
        "url": path
      };
      this.$store.commit('menu/addActiveMenu', param);
      this.$store.commit('menu/setActiveMenu', path);
    },
    toTask() {
      var param = {
        "name": "教师课程管理",
        "url": "/task"
      };
      this.$store.commit('menu/addActiveMenu', param);
      this.$router.push("/teacherTask");
      this.$store.commit('menu/setActiveMenu', "/teacherTask");
    },
    toStudent() {
      var param = {
        "name": "学生管理",
        "url": "/student"
      };
      this.$store.commit('menu/addActiveMenu', param);
      this.$router.push("/student");
      this.$store.commit('menu/setActiveMenu', "/student");
    },
    toTeacher() {
      var param = {
        "name": "教师管理",
        "url": "/teacher"
      };
      this.$store.commit('menu/addActiveMenu', param);
      this.$router.push("/teacher");
      this.$store.commit('menu/setActiveMenu', "/teacher");
    },
    initProgressChart() {
      this.$nextTick(() => {
        const dom = document.getElementById("progress-chart");
        if (!dom) {
          console.error('Progress chart DOM not found');
          return;
        }

        // 先销毁旧实例
        if (this.progressChart) {
          this.progressChart.dispose();
        }
        this.progressChart = echarts.init(dom);
        const option = {
          tooltip: {
            trigger: 'axis',
            axisPointer: {
              type: 'cross',
              crossStyle: {
                color: '#999'
              }
            }
          },
          legend: {
            data: ['计划进度', '实际进度', '平均成绩']
          },
          grid: [{
            top: '15%',
            height: '30%'
          }, {
            top: '55%',
            height: '30%'
          }],
          xAxis: [{
            type: 'category',
            data: ['第1周', '第2周', '第3周', '第4周', '第5周', '第6周', '第7周'],
            axisPointer: {
              type: 'shadow'
            },
            gridIndex: 0
          }, {
            type: 'category',
            data: ['0-59', '60-69', '70-79', '80-89', '90-100'],
            gridIndex: 1
          }],
          yAxis: [{
            type: 'value',
            name: '进度(%)',
            min: 0,
            max: 100,
            gridIndex: 0
          }, {
            type: 'value',
            name: '人数',
            gridIndex: 1
          }],
          series: [{
            name: '计划进度',
            type: 'line',
            smooth: true,
            data: [15, 30, 45, 60, 75, 90, 100],
            lineStyle: {
              color: '#8884d8',
              width: 3,
              type: 'dashed'
            },
            itemStyle: {
              color: '#8884d8'
            },
            xAxisIndex: 0,
            yAxisIndex: 0
          }, {
            name: '实际进度',
            type: 'line',
            smooth: true,
            data: [10, 25, 40, 50, 65, 80, 95],
            lineStyle: {
              color: '#82ca9d',
              width: 3
            },
            itemStyle: {
              color: '#82ca9d'
            },
            areaStyle: {
              color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                offset: 0,
                color: 'rgba(130, 202, 157, 0.3)'
              }, {
                offset: 1,
                color: 'rgba(130, 202, 157, 0.1)'
              }])
            },
            xAxisIndex: 0,
            yAxisIndex: 0
          }, {
            name: '成绩分布',
            type: 'bar',
            barWidth: '60%',
            data: [2, 4, 10, 15, 8],
            itemStyle: {
              color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                offset: 0,
                color: '#83bff6'
              }, {
                offset: 0.5,
                color: '#188df0'
              }, {
                offset: 1,
                color: '#188df0'
              }])
            },
            xAxisIndex: 1,
            yAxisIndex: 1
          }]
        };
        this.progressChart.setOption(option);
      });
    },
    initResourceChart() {
      this.$nextTick(() => {
        const dom = document.getElementById("resource-chart");
        if (!dom) {
          console.error('Resource chart DOM not found');
          return;
        }

        // 先销毁旧实例
        if (this.resourceChart) {
          this.resourceChart.dispose();
        }
        this.resourceChart = echarts.init(dom);
        const option = {
          title: {
            text: '资源类型分布',
            left: 'center',
            top: 10
          },
          series: {
            name: '资源分布',
            type: 'sunburst',
            data: [{
              name: '课程资源',
              children: [{
                name: '教学视频',
                value: 35,
                itemStyle: {
                  color: '#5470c6'
                }
              }, {
                name: '课件资料',
                value: 25,
                itemStyle: {
                  color: '#91cc75'
                }
              }, {
                name: '测试题库',
                value: 20,
                itemStyle: {
                  color: '#fac858'
                }
              }, {
                name: '阅读材料',
                value: 15,
                itemStyle: {
                  color: '#ee6666'
                }
              }, {
                name: '其他资源',
                value: 5,
                itemStyle: {
                  color: '#73c0de'
                }
              }]
            }],
            radius: [0, '90%'],
            label: {
              rotate: 'radial'
            },
            levels: [{}, {
              r0: '15%',
              r: '45%',
              itemStyle: {
                borderWidth: 2
              },
              label: {
                rotate: 'tangential'
              }
            }, {
              r0: '45%',
              r: '80%',
              label: {
                align: 'right'
              }
            }, {
              r0: '80%',
              r: '82%',
              label: {
                position: 'outside',
                padding: 3,
                silent: false
              },
              itemStyle: {
                borderWidth: 3
              }
            }]
          }
        };
        this.resourceChart.setOption(option);
      });
    },
    // 添加防抖的 resize 方法
    handleResize: debounce(function () {
      this.progressChart && this.progressChart.resize();
      this.resourceChart && this.resourceChart.resize();
    }, 200)
  },
  // 如果路由变化但组件未销毁，需要监听路由变化重新初始化
  watch: {
    '$route'() {
      this.initProgressChart();
      this.initResourceChart();
    },
    // 如果课程选择变化，可以重新加载数据
    courseSelect(newVal) {
      if (newVal) {
        this.initProgressChart();
      }
    }
  },
  mounted() {
    // 初始化图表
    this.initProgressChart();
    this.initResourceChart();

    // 获取首页顶部数据
    getIndexData({
      type: 1
    }).then(res => {
      if (res.code == 1000) {
        this.top = res.data;
      }
    });

    // 获取学生性别数据
    getIndexSexData({
      type: 1
    }).then(res => {
      if (res.code == 1000) {
        this.sex = res.data;
        this.pieChart = echarts.init(document.getElementById("pie-chart"));
        var optionPie = {
          tooltip: {
            trigger: 'item'
          },
          legend: {
            center: 'center'
          },
          series: [{
            name: '学生性别分析',
            type: 'pie',
            radius: '50%',
            data: this.sex,
            emphasis: {
              itemStyle: {
                shadowBlur: 10,
                shadowOffsetX: 0,
                shadowColor: 'rgba(0, 0, 0, 0.5)'
              }
            }
          }]
        };
        this.pieChart.setOption(optionPie);
      }
    });

    // 获取任务图表数据
    getTaskChart({
      type: 1
    }).then(res => {
      if (res.code == 1000) {
        this.tasks = res.data.tasks;
        this.nums = res.data.nums;
        this.myChart = echarts.init(document.getElementById("chart"));
        var option = {
          tooltip: {
            trigger: 'axis'
          },
          legend: {
            data: ['学生数量', '学生数量']
          },
          backgroundColor: '#fff',
          xAxis: [{
            type: 'category',
            color: '#59588D',
            data: this.tasks,
            axisLine: {
              lineStyle: {
                color: 'rgba(107,107,107,0.37)'
              }
            },
            axisTick: {
              show: true
            }
          }],
          yAxis: [{
            axisLine: {
              lineStyle: {
                color: 'rgba(107,107,107,0.37)'
              }
            },
            axisTick: {
              show: true
            },
            splitLine: {
              lineStyle: {
                color: 'rgba(131,101,101,0.2)',
                type: 'dashed'
              }
            }
          }],
          series: [{
            data: this.nums,
            type: 'line',
            name: '折线图',
            symbol: 'none',
            areaStyle: {
              color: "#E6F8EE"
            },
            lineStyle: {
              color: '#3AC977',
              width: 2,
              shadowColor: 'rgba(0, 0, 0, 0.3)',
              //设置折线阴影
              shadowBlur: 15,
              shadowOffsetY: 20
            },
            zlevel: 1
          }, {
            type: 'bar',
            data: this.nums,
            barWidth: '15px',
            name: '柱状图',
            itemStyle: {
              normal: {
                color: function (params) {
                  //展示正值的柱子，负数设为透明
                  let colorArr = params.value > 0 ? ['#7866BE', '#7866BE'] : ['rgba(0,0,0,0)', 'rgba(0,0,0,0)'];
                  return new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                    offset: 0,
                    color: colorArr[0] // 0% 处的颜色
                  }, {
                    offset: 1,
                    color: colorArr[1] // 100% 处的颜色
                  }], false);
                },
                barBorderRadius: [30, 30, 0, 0]
              }
            },
            label: {
              normal: {
                show: true,
                fontSize: 13,
                color: '#333',
                position: 'top'
              }
            },
            zlevel: 2
          }]
        };
        this.myChart.setOption(option);
      }
    });

    // 获取任务列表数据
    getTaskIndexList({
      type: 1
    }).then(res => {
      if (res.code == 1000) {
        this.taskList = res.data.slice(0, 5);
        this.taskCard = res.data.slice(0, 3);
      }
    });

    // 窗口大小变化时重绘图表
    window.addEventListener('resize', this.handleResize);
    window.addEventListener('resize', () => {
      if (this.myChart && typeof this.myChart.resize === 'function') {
        this.myChart.resize();
      }
      if (this.pieChart && typeof this.pieChart.resize === 'function') {
        this.pieChart.resize();
      }
    });
  },
  beforeDestroy() {
    window.removeEventListener('resize', this.handleResize);
    if (this.progressChart) {
      this.progressChart.dispose();
    }
    if (this.resourceChart) {
      this.resourceChart.dispose();
    }
    if (this.myChart && typeof this.myChart.dispose === 'function') {
      this.myChart.dispose();
    }
    if (this.pieChart && typeof this.pieChart.dispose === 'function') {
      this.pieChart.dispose();
    }
  }
};
