import { getMenuByUser } from '@/api/api'

const state = {
  activeMenuArrary: [],
  activeMenu: "/index",
  flag: false,
  menus: [],
  routes: [],
  displayMenus: [],
  btnMenus: []
}

const getters = {
  getMenu: state => state.menus,
  getFlag: state => state.flag,
  getRoutes: state => state.routes,
  getDisplayMenus: state => state.displayMenus,
  getBtnMenus: state => state.btnMenus
}

const mutations = {
  setActiveMenu(state, menu) {
    state.activeMenu = menu
  },
  addActiveMenu(state, menu) {
    state.activeMenuArrary.push(menu)
  },
  setActiveMenuArrary(state, menu) {
    state.activeMenuArrary = menu
  },
  reduceActiveMenu(state, i) {
    state.activeMenuArrary.splice(i, 1)
  },
  setMenus(state, menus) {
    state.menus = menus
  },
  setFlag(state, flag) {
    state.flag = flag
  },
  setRoutes(state, routes) {
    state.routes = routes
  },
  setDisplayMenus(state, menus) {
    state.displayMenus = menus
  },
  setBtnMenus(state, btn) {
    state.btnMenus = btn
  }
}

const actions = {
  generateRoutes({ commit }) {
    return new Promise((resolve, reject) => {
      console.group('[ËèúÂçïÂàùÂßãÂåñ] ÂºÄÂßãÂä†ËΩΩËèúÂçïÊï∞ÊçÆ')
      
      getMenuByUser().then(res => {
        console.log('üîπ ÂéüÂßãAPIÂìçÂ∫îÊï∞ÊçÆ:', JSON.parse(JSON.stringify(res)))

        if (res.code !== 1000) {
          console.error('‚ùå Ëé∑ÂèñËèúÂçïÂ§±Ë¥•:', res.message)
          console.groupEnd()
          return reject(new Error(res.message))
        }

        // ÊâìÂç∞ÂÆåÊï¥ËèúÂçïÊ†ë
        console.log('üå≤ ÂÆåÊï¥ËèúÂçïÊï∞ÊçÆ:', JSON.parse(JSON.stringify(res.data)))
        commit("setMenus", res.data)
        commit("setFlag", true)

        // Ë∑ØÁî±ÁîüÊàê
        const routes = filterRoutes(res.data)
        console.log('üõ£Ô∏è ÁîüÊàêÁöÑË∑ØÁî±ÈÖçÁΩÆ:', routes)
        commit("setRoutes", routes)

        // ÊòæÁ§∫ËèúÂçïÂ§ÑÁêÜ
        const displayMenus = filterMenus(res.data)
        console.log('üìã ÊúÄÁªàÊòæÁ§∫ÁöÑËèúÂçï:', displayMenus)
        commit("setDisplayMenus", displayMenus)

        // ËÆæÁΩÆÈªòËÆ§ËèúÂçï
        setDefaultActiveMenu(commit, displayMenus)

        // ÊåâÈíÆÊùÉÈôê
        const btnMenus = filterBtnMenus(res.data)
        console.log('üõë ÊåâÈíÆÊùÉÈôêÂàóË°®:', btnMenus)
        commit("setBtnMenus", btnMenus)

        console.groupEnd()
        resolve(routes)
      }).catch(err => {
        console.error('üí• ËèúÂçïÂä†ËΩΩÂºÇÂ∏∏:', err)
        console.groupEnd()
        reject(err)
      })
    })
  }
}

/* Ê†∏ÂøÉÂ∑•ÂÖ∑ÂáΩÊï∞ */
function filterBtnMenus(menus) {
  return menus
    .filter(item => item.menuType === 2)
    .map(item => ({ ...item })) // ÊµÖÊã∑Ë¥ù
}

function filterMenus(menus) {
  console.groupCollapsed('üîç Ê≠£Âú®ËøáÊª§ËèúÂçï...')
  const result = []
  
  menus.forEach(item => {
    const isValid = (
      item.menuType !== 2 &&          // ÈùûÊåâÈíÆ
      item.visible === 0 &&           // ÂèØËßÅ
      (item.status === 0 || item.status === null) && // ÂêØÁî®ÊàñÊú™ËÆæÁΩÆ
      item.parentId === "0"           // È°∂Á∫ßËèúÂçï
    )

    console.log(`Ê£ÄÊü•ËèúÂçï: ${item.menuName}`, {
      id: item.id,
      Á±ªÂûã: ['ÁõÆÂΩï','ËèúÂçï','ÊåâÈíÆ'][item.menuType] || 'Êú™Áü•',
      ÂèØËßÅ: item.visible === 0 ? 'ÊòØ' : 'Âê¶',
      Áä∂ÊÄÅ: item.status === 0 ? 'ÂêØÁî®' : item.status === 1 ? 'ÂÅúÁî®' : 'Êú™ËÆæÁΩÆ',
      Áà∂ID: item.parentId,
      ÁªìÊûú: isValid ? '‚úÖ ÈÄöËøá' : '‚ùå ËøáÊª§'
    })

    if (isValid) {
      const menuItem = { ...item, children: [] }
      if (item.menuType === 0) {
        menuItem.children = filterChildMenus(item, menus)
      }
      result.push(menuItem)
    }
  })

  console.log('ËøáÊª§ÁªìÊûúÊï∞Èáè:', result.length)
  console.groupEnd()
  return result
}

// ‰øÆÊ≠£ÂêéÁöÑ filterChildMenus ÂáΩÊï∞ÔºàÂîØ‰∏ÄÂÆûÁé∞Ôºâ
function filterChildMenus(parent, allMenus) {
  return allMenus
    .filter(item => 
      item.parentId === parent.id && 
      item.menuType !== 2 && 
      item.visible === 0
    )
    .map(item => {
      const child = { ...item }
      if (child.menuType === 0) {
        child.children = filterChildMenus(child, allMenus)
      }
      return child
    })
}

function filterRoutes(menus) {
  if (!menus || menus.length <= 0) {
    console.warn('‚ö†Ô∏è Á©∫ËèúÂçïÊï∞ÊçÆÔºåËøîÂõû404Ë∑ØÁî±')
    return [{ path: '*', redirect: '/404' }]
  }

  const routes = [{
    path: '/',
    component: () => import('@/views/layout/index'),
    redirect: '/index',
    children: []
  }]

  menus.forEach(item => {
    if (item.menuType === 1) { // Âè™Â§ÑÁêÜËèúÂçïÁ±ªÂûã
      const route = {
        path: item.routeUrl,
        name: item.menuName,
        component: loadView(item.componentUrl),
        meta: {
          title: item.menuName,
          icon: item.icon
        }
      }

      item.target === 1 
        ? routes[0].children.push(route)
        : routes.push(route)
    }
  })

  routes.push({ path: '*', redirect: '/404' })
  return routes
}

function loadView(viewPath) {
  if (!viewPath) {
    console.error('üö´ Á©∫ÁªÑ‰ª∂Ë∑ØÂæÑÔºåËøîÂõû404È°µÈù¢')
    return () => import('@/views/error/404')
  }

  // Áªü‰∏ÄË∑ØÂæÑÊ†ºÂºè
  const normalizedPath = viewPath.startsWith('/') ? viewPath : `/${viewPath}`
  const fullPath = `@/views${normalizedPath}`

  if (process.env.NODE_ENV === 'development') {
    return (resolve) => {
      require([fullPath], resolve, (err) => {
        console.error(`Âä†ËΩΩÁªÑ‰ª∂Â§±Ë¥•: ${fullPath}`, err)
        resolve(require('@/views/error/404').default)
      })
    }
  } else {
    return () => import(/* webpackChunkName: "view-[request]" */ fullPath)
      .catch(err => {
        console.error(`Âä†ËΩΩÁªÑ‰ª∂Â§±Ë¥•: ${fullPath}`, err)
        return import('@/views/error/404')
      })
  }
}

function setDefaultActiveMenu(commit, menus) {
  const findFirstValid = (menuList) => {
    for (const item of menuList) {
      if (item.menuType === 1) return item
      if (item.children?.length) {
        const found = findFirstValid(item.children)
        if (found) return found
      }
    }
    return null
  }

  const defaultMenu = findFirstValid(menus) || { 
    routeUrl: '/index', 
    menuName: 'È¶ñÈ°µ' 
  }

  console.log('‚≠ê ËÆæÁΩÆÈªòËÆ§ËèúÂçï:', defaultMenu)
  commit("setActiveMenu", defaultMenu.routeUrl)
  commit("addActiveMenu", {
    name: defaultMenu.menuName,
    url: defaultMenu.routeUrl
  })
}

export default {
  namespaced: true,
  state,
  getters,
  mutations,
  actions
}
